import react, { useEffect, useState } from "react";

function Taimer() {

    const [minute, setMinutState] = useState(0);
    const [second, setSecondState] = useState(0);
    // const [timeStart, setTimedState] = useState(false);

    // function Start(){
    //     setTimedState(!timeStart);
    // }

    let timeStart = false; 

    function time(){
        timeStart = !timeStart;
        useEffect(() => {
            while (timeStart){
                setSecondState(second + 1);
                if (second == 60){
                    setMinutState(minute + 1);
                    setSecondState(second = 0);
                }
            }
        },[second, minute]);
    }

    return (
      <div className="taimer">
        <h1>{minute}:{second}</h1>
        <button className="StartButton" onClick={time}>Start</button>
      </div>
    );
    }
  
  export default Taimer;
