import React, { useState } from "react";

function Achievements() {

    let array = [];

    let card = document.getElementById('Cards_container');
    card.onclick = function(){
        array.push(this.target.parentNode.parentNode.id);

        if (array.length == 2){
            if (array[0] == array[1]){
                let ach = document.getElementById(array[0]);
                ach.classList.remove('invisible');
            } 
            array.splice(0, 2);
        }
    }

    return (
        <div className="achievements">
           <div className="achievement invisible" id="We">We</div>
           <div className="achievement invisible" id="Nada">Nada</div>
           <div className="achievement invisible" id="Gopota">Gopota</div>
           <div className="achievement invisible" id="Vlad">Vlad</div>
           <div className="achievement invisible" id="Book">Book</div>
           <div className="achievement invisible" id="Cartochka">Cartochka</div>
        </div>
  );
}

export default Achievements
