import react, { useEffect, useState } from "react";

function Taimer() {

    let [minute, setMinutState] = useState(0);
    let [second, setSecondState] = useState(0);
    const [timeStart, setTimedState] = useState(false);
    
    function Start(){

      setTimedState(true);

      let block = document.querySelector('.Cards_container');
      block.classList.remove('invisible');
        
      useEffect(() => {
        setTimeout(() => { 
            if (timeStart == false) return;
            if (second == 60) { 
                setSecondState(second = 0);
            }
            setSecondState(second + 1);
          }, 1000);
        });

      useEffect(() => {
        setTimeout(() => { 
            if (timeStart == false) return;
            setMinutState(minute + 1);
          }, 60000);
      });

      useEffect(() => {
          let inverted = document.querySelectorAll('.inverted');
          let all = document.querySelectorAll('.card-container');
          if (inverted.length == all.length){
          setTimedState(false);
          }
      });

  }

    function Restart(){
      let inv = document.querySelectorAll('.inverted');

      for (let element of inv){
        element.classList.remove('inverted');
        element.classList.remove('invisible');
      }

      setSecondState(second = 0);
      setMinutState(minute = 0);

      let visible = document.querySelectorAll('.achievement');
      for (let element of visible){
        if (!element.classList.contains('invisible')){
          element.classList.add('invisible');
        }
      }
    }

    return (
      <div className="taimer">
        <h1>{minute}:{second}</h1>
        <button className="StartButton" onClick={Start}>Start</button>
        <button className="Restart" onClick={Restart}>Restsrt</button>
      </div>
    );
    }
  
  export default Taimer;
