import React, { useState } from "react";
import Card from "./Components/Cards";
import './styles/style.css';

function App() {

   let array = [];
   let cont = [];
   let val = [];

   function count(event){
    array.push(event.target.parentNode.parentNode.id);
    console.log(array);

    if (array.length == 2){
        if (array[0] == array[1]){
          console.log('You find identically card');
        } else {
          let ty = value(array);
          console.log(ty);
          console.log('Try again');
          setTimeout(() => { 
            for(let i = 0; i < ty.length; i++){
              ty[i].classList.remove('inverted');   
          }
        }, 1000);
      }
      array.splice(0, 2);
      cont.splice(0, 2);
      val.splice(0, 2);
  }
}

  function value(array){
    let value = document.querySelectorAll('.inverted');
    value.forEach(element => {
      for(let i = 0; i < array.length; i++){
        if (element.id == array[i]){
          val.push(element);
        }
      }
    });
    return val;
  }


  return (
    <div className="App" onClick={count}>
      <Card image = "/images/Nada.jpg" id = 'Nada'/>
