      <Card image = "/images/Gop-stop.jpg" id = 'Gopota'/>
      <Card image = "/images/Cartochka.jpg" id = 'Cartochka'/>
      <Card image = "/images/Book.jpg" id = 'Book'/>
      <Card image = "/images/Cartochka.jpg" id = 'Cartochka'/>
      <Card image = "/images/Nada.jpg" id = 'Nada'/>
    </div>
  );
}

export default App;
