import React, { useState } from "react";
import Card from "./Components/Cards";
import './styles/style.css';

function App() {

  let array = [];

   function count(event){
    array.push(event.target.parentNode.parentNode.id);
    console.log(array);

    if (array.length == 2){
      let value = document.querySelectorAll('.inverted');

      console.log(value);

      if (array[0] == array[1]){
        // alert('You find identically card');
      } else {
        // alert('Try again');
          for(let i = 0; i < value.length; i++){
            value[i].classList.remove('inverted');
        }
      }




      array.splice(0, 2);

    }

  }

  return (
    <div className="App" onClick={count}>
      <Card image = "/images/Nada.jpg" id = 'Nada'/>
      <Card image = "/images/Book.jpg" id = 'Book'/>
      <Card image = "/images/Book.jpg" id = 'Book'/>
      <Card image = "/images/Nada.jpg" id = 'Nada'/>
    </div>
  );
}

export default App;
