import react, { useEffect, useState } from "react";

function Taimer() {

    const [minute, setMinutState] = useState(0);
    const [second, setSecondState] = useState(0);
    const [timeStart, setTimedState] = useState(0);
    
    function Start(){
      setTimedState(timeStart + 2);
      let block = document.querySelector('.Cards_container');
      let start = document.querySelector('.Start');
      let restart = document.querySelector('.Restart');
      block.classList.remove('invisible');
      start.classList.add('block');
      restart.classList.add('block');
    }
        
        setTimeout(() => { 
            if (timeStart == 0) return;
            if (timeStart == 1){
               setSecondState(second - second);
               setTimedState(timeStart + 1);
            }
            if (timeStart == 2){
              setSecondState(second + 1);
              console.log(second);
            }
          }, 1000);

    

        
        setTimeout(() => { 
            if (timeStart == 0) return;
            if (timeStart == 1){
              setMinutState(minute - minute);
              setTimedState(timeStart + 1);
           }
           if (timeStart == 2){
            setMinutState(minute + 1);
           }
          }, 60000);


    useEffect(() => {
        let inverted = document.querySelectorAll('.inverted');
        let all = document.querySelectorAll('.card-container');
        if (inverted.length == all.length){
        setTimedState(timeStart - timeStart);
         }
    });

    function Restart(){
      let questuion = window.confirm('Вы действительно хотите начать заного?');

      if (questuion){
        let inv = document.querySelectorAll('.inverted');
        for (let element of inv){
          element.classList.remove('inverted');
          element.classList.remove('invisible');
        }

        let visible = document.querySelectorAll('.achievement');
        for (let element of visible){
          if (!element.classList.contains('invisible')){
            element.classList.add('invisible');
          }
        }

        setTimedState(timeStart - 1);
    } 
    }

    return (
      <div className="taimer">
        <h1>{minute}:{second}</h1>
        <div className="buttons">
          <button className="Start button" onClick={Start}>Start</button>
          <button className="Restart button" onClick={Restart}>Restart</button>
        </div>
      </div>
    );
    }
  
  export default Taimer;
