import Card from "./Components/Cards";
import './styles/style.css';

function App() {

  let array = [];

   function count(event){
    array.push(event.target.parentNode.parentNode.id);
    console.log(array);
    console.log(array.length);
    if (array.length == 2){
      if (array[0] == array[1]){
        alert('You find identically card');
      } else {
        alert('Try again');
        array.forEach(function remove(item){
          let value = document.getElementById(item);
          console.log(value);
          // value.classList.remove('inverted');
        });

      }

      array.splice(0, 2);

    }

  }

  return (
    <div className="App" onClick={count}>
      <Card image = "/images/Nada.jpg" id = 'Nada'/>
      <Card image = "/images/Book.jpg" id = 'Book'/>
      <Card image = "/images/Book.jpg" id = 'Book'/>
      <Card image = "/images/Nada.jpg" id = 'Nada'/>
    </div>

  );
}

export default App;
