import { useEffect, useState } from "react";

function Timer() {

    let [minute, setMinutState] = useState(0);
    let [second, setSecondState] = useState(0);
    let [timeStart, setTimedState] = useState(0);
    
    function Start(){
      setTimedState(timeStart + 2);

      let block = document.querySelectorAll('.Cards-container');
      for (let element of block){
        element.classList.remove('stop');
      }

      let start = document.querySelector('.Start');
      start.classList.add('block');

      let restart = document.querySelector('.Restart');
      restart.classList.remove('block');

      let stop = document.querySelector('.Stop');
      stop.classList.remove('block');
    }

    function Stop(event){
      let block = document.querySelector('.Cards_container');
      if (timeStart === 2){
        setTimedState(timeStart - 2);
        event.target.textContent = 'Start';
        block.classList.add('stop');
      }
      if (timeStart === 0){
        setTimedState(timeStart + 2);
        event.target.textContent = 'Stop';
        block.classList.remove('stop');
      }
    }
        
    setTimeout(() => { 
      if (timeStart === 0) return;
      if (timeStart === 1){
        setSecondState(second = 0);
        setMinutState(minute = 0);
        setTimedState(timeStart + 1);
      }
      if (timeStart === 2){
        setSecondState(second + 1);
      }
      if (second === 59){
        setSecondState(second = 0);
        setMinutState(minute + 1);
      }
    }, 1000);

    useEffect(() => {
        let inverted = document.querySelectorAll('.inverted');
        let all = document.querySelectorAll('.card-container');
        if (inverted.length === all.length){
        setTimedState(timeStart = 0);
         }
    });

    function Restart(){
      let questuion = window.confirm('Вы действительно хотите начать заново?');

      if (questuion){
        let inv = document.querySelectorAll('.inverted');
        for (let element of inv){
          element.classList.remove('inverted');
          element.classList.remove('invisible');
        }

        let visible = document.querySelectorAll('.achievement');
        for (let element of visible){
          if (!element.classList.contains('invisible')){
            element.classList.add('invisible');
          }
        }

        setTimedState(timeStart - 1);
    } 
    }

    return (
      <div className="taimer">
        <h1>{minute}:{second}</h1>
        <div className="buttons">
          <button className="Start button" onClick={Start}>New game</button>
          <button className="Restart button block" onClick={Restart}>Restart</button>
          <button className="Stop button block" onClick={Stop}>Stop</button>
        </div>
      </div>
    );
    }
  
  export default Timer;
