import react, { useEffect, useState } from "react";

function Taimer() {

    const [minute, setMinutState] = useState(0);
    let [second, setSecondState] = useState(0);
    const [timeStart, setTimedState] = useState(false);
    

        
        setTimeout(() => { 
            if (timeStart == false) return;
            if (second == 60) { 
                setSecondState(second = 0);
            }
            setSecondState(second + 1);
          }, 1000);

    
    useEffect(() => {
        if (timeStart == false) return;
        setTimeout(() => { 
            setMinutState(minute + 1);
          }, 60000);
    });

    return (
      <div className="taimer">
        <h1>{minute}:{second}</h1>
        <button className="StartButton" onClick={() => {setTimedState(true)}}>Start</button>
        <button className="StopButton" onClick={() => {setTimedState(false)}}>Stop</button>
      </div>
    );
    }
  
  export default Taimer;
