import react, { useState } from "react";

function Taimer() {

    const [minute, setMinutState] = useState(0);
    const [second, setSecondState] = useState(0);
    const [timeStart, setTimedState] = useState(false);

    function Start(){
        setTimedState(timeStart = !timeStart);
    }

    while (timeStart){
        setSecondState(second ++);
        if (second == 60){
            setMinutState(minute ++);
            setSecondState(second = 0);
        }
    }


    return (
      <div className="taimer">
        <h1>{minute}:{second}</h1>
        <button className="StartButton" onClick={Start}>Start</button>
      </div>
    );
  }
  
  export default Taimer;
