import React, { useState } from "react";

function Card() {

    function alt(event){
        event.currentTarget.classList.add ('new') 
        alert(event.currentTarget.className);    
    }

    return (
        <div className="card-container" onClick={alt}>
            <div className="front-side">
                <img src="/images/Vlad.jpg"/>
            </div>
            <div className="back-side">
                <img src="/images/Nada.jpg"/>
            </div>
        </div>
  );
}

export default Card
