import React, { useState } from "react";

function Achievements() {

    let array = [];

    let card = document.getElementById('Cards_container');
    card.onclick = function(){
        array.push(this.target.parentNode.parentNode.id);

        if (array.length == 2){
            if (array[0] == array[1]){
                let ach = document.getElementById(array[0]);
                ach.classList.remove('invisible');
            } 
            array.splice(0, 2);
        }
    }

    return (
        <div className="achievements">
           
        </div>
  );
}

export default Achievements
