import react from "react";
import Card from "./Cards";

function Cards_container() {

    let array = [];
 
    function count(event){
     array.push(event.target.parentNode.parentNode.id);
     if (array.length == 2){
      let value = document.querySelectorAll('.inverted');
        if (array[0] == array[1]){
          for(let i = 0; i < value.length; i++){
            value[i].classList.add('invisible');
          }
          console.log(array[0]);
          let card = document.getElementsByName(array[0]);
          console.log(card);
          // card.classList.remove('invisible');
          console.log('You find identically card');
        } else {
          console.log('Try again');
            for(let i = 0; i < value.length; i++){
              for(let j = 0; j < array.length; j++){
                if (value[i].id == array[j]){
                  setTimeout(() => { 
                    value[i].classList.remove('inverted');
                  }, 1000);
                }
              }
            }
       }
       array.splice(0, 2);
   }
 }
 
   return (
     <div className="Cards_container" onClick={count} id='Cards_container'>
       <Card image = "/images/Nada.jpg" id = 'Nada'/>
       <Card image = "/images/Gop-stop.jpg" id = 'Gopota'/>
       <Card image = "/images/Book.jpg" id = 'Book'/>
       <Card image = "/images/VlaDick.jpg" id = 'Vlad'/>
       <Card image = "/images/VlaDick.jpg" id = 'Vlad'/>
       <Card image = "/images/We.jpg" id = 'We'/>
       <Card image = "/images/We.jpg" id = 'We'/>
       <Card image = "/images/Gop-stop.jpg" id = 'Gopota'/>
       <Card image = "/images/Cartochka.jpg" id = 'Cartochka'/>
       <Card image = "/images/Book.jpg" id = 'Book'/>
       <Card image = "/images/Cartochka.jpg" id = 'Cartochka'/>
       <Card image = "/images/Nada.jpg" id = 'Nada'/>
     </div>
   );
 }
 
 export default Cards_container;
