import React, { useState, useEffect} from "react";
import { useParams } from "react-router-dom";

function Profile() {

  const [info, SetInfo] = useState('');

  const params = useParams();

  const prodId = params.id;

  useEffect(async() => {
  
    let response = await fetch('https://jsonplaceholder.typicode.com/users');
    let text = await response.json();

    let array = text.find(p=>p.id == prodId);

    SetInfo(array.map((matirials, id) => 
    <div className="Profile">
      <div>Name</div>
      <input type="text" value={info.name}></input>
      <div>User name</div>
      <input type="text" value={info.username}></input>
      <div>E-mail</div>
      <input type="text" value={info.email}></input>
      <div>Street</div>
      <input type="text" value={info.address}></input>
      <div>City</div>
      <input type="text" value={info.address}></input>
      <div>Zip code</div>
      <input type="text" value={info.address}></input>
      <div>Phone</div>
      <input type="text" value={info.phone}></input>
      <div>Website</div>
      <input type="text" value={info.website}></input>
      <div>Comment</div>
      <input type="text"></input>
  </div>
      ));
  });

  return (
    <div className="Profile">
        <div>Name</div>
        <input type="text" value={info.name}></input>
        <div>User name</div>
        <input type="text" value={info.username}></input>
        <div>E-mail</div>
        <input type="text" value={info.email}></input>
        <div>Street</div>
        <input type="text" value={info.address}></input>
        <div>City</div>
        <input type="text" value={info.address}></input>
        <div>Zip code</div>
        <input type="text" value={info.address}></input>
        <div>Phone</div>
        <input type="text" value={info.phone}></input>
        <div>Website</div>
        <input type="text" value={info.website}></input>
        <div>Comment</div>
        <input type="text"></input>
    </div>
  );
}

export default Profile;
