import React, { useState } from "react";
import '/src/styles/style.css';

function Counter() {

  const [count, setCount] = useState(0)

  function Up(){
    setCount(count + 1);
  }

  function Down(){
    setCount(count - 1);
  }

  return (
    <div>
      <h1>{count}</h1>
      <button className="btn_green" onClick={Up}>Up</button>
      <button className="btn_red" onClick={Down}>Down</button>
    </div>
  );
}

export default Counter
