import React, { useState } from "react";

function Card(props) {

    function alt(event){ 
        alert(event.currentTarget.name)
        if(!event.currentTarget.classList.contains('inverted')){
            event.currentTarget.classList.add('inverted');
        } else {
            event.currentTarget.classList.remove('inverted');
        } 
    }

    return (
        <div className="card-container" onClick={alt} name = {props.name}>
            <div className="back-side">
                <img src={props.image}/>
            </div>
            <div className="front-side">
                <img src="/images/Vlad.jpg"/>
            </div>
            
        </div>
  );
}

export default Card
