import { useEffect, useState } from "react";
import Card from "./Cards";
import cardsProps from "../Props/cardsProps";

function Cards_container() {

    const [cardsList, SetCardsList] = useState('');
    const [select, SetSelect] = useState(0);

    useEffect(() => {
      let props = cardsProps();
      props.sort(() => Math.random() - 0.5);
      SetCardsList(props.map(prop => (
        <Card image = {prop.image} id = {prop.id}/>
      )));
    }, []);

    let array = [];

    function showAchievement(array){
      let card = document.querySelector(`div[name=${array[0]}]`);
      card.classList.remove('invisible');
    }

    function hideCard(value){
      for(let i = 0; i < value.length; i++){
        value[i].classList.add('invisible');
      }
    }

    function closeCard(array, value){
      for(let i = 0; i < value.length; i++){
        for(let j = 0; j < array.length; j++){
          if (value[i].id === array[j]){
            setTimeout(() => { 
              value[i].classList.remove('inverted');
            }, 1000);
          }
        }
      }
    }

    function changeCardState(array, value){
      if (array[0] === array[1]){
        hideCard(value);
        showAchievement(array);
      } else {
        closeCard(array, value);
     }
    }
 
    function count(event){
      array.push(event.target.parentNode.parentNode.id);
      let value = document.querySelectorAll('.inverted');
      if (array.length === 2){
        changeCardState(array, value);
        array.splice(0, 2);
      }
    }
 
   return (
    <div>
      <div className="Cards_container stop" onClick={count} id='Cards_container'>
        {cardsList}
      </div>
      <div>

            <select className="dropdown-select">
              <option value="0">Choose level</option>
              <option value="1">Easy</option>
              <option value="2">Normal</option>
              <option value="3">Hard</option>
            </select>

      </div>
    </div>
   );
 }
 
 export default Cards_container;
