import React, { useState, useEffect} from "react";
import { useParams } from "react-router-dom";

function Profile() {

  const [info, SetInfo] = useState('');

  const params = useParams();

  const prodId = params.id;

  useEffect(async() => {
  
    let response = await fetch('https://jsonplaceholder.typicode.com/users');
    let text = await response.json();

    let array = text.find(p=>p.id == prodId);

    SetInfo(array.map((matirials, id) => {
      if (id == prodId){
      
      <div className="Profile">
        <div>Name</div>
        <input type="text" value={matirials.name}></input>
        <div>User name</div>
        <input type="text" value={matirials.username}></input>
        <div>E-mail</div>
        <input type="text" value={matirials.email}></input>
        <div>Street</div>
        <input type="text" value={matirials.address.street}></input>
        <div>City</div>
        <input type="text" value={matirials.address.city}></input>
        <div>Zip code</div>
        <input type="text" value={matirials.address.zipcode}></input>
        <div>Phone</div>
        <input type="text" value={matirials.phone}></input>
        <div>Website</div>
        <input type="text" value={matirials.website}></input>
        <div>Comment</div>
        <input type="text"></input>
    </div>
    }}));

  return (
    <div>
      {info}
    </div>
  );
})
}

export default Profile;
